library(testthat)context("sections")test_that("Truthy",{  data <- list(boolean=TRUE)  str <- whisker.render('"{{#boolean}}This should be rendered.{{/boolean}}"', data)  expect_equal(str, '"This should be rendered."')})test_that("Falsey",{  data <- list(boolean=FALSE)  str <- whisker.render('"{{#boolean}}This should not be rendered.{{/boolean}}"', data)  print(str)  expect_equal(str, '""')})